{% if true %}
        {% for command_class in Tourmaline::Client.subclasses %}
          {% for method in command_class.methods %}

            # Handle `OnCallbackQuery` annotation
            {% for ann in method.annotations(OnCallbackQuery) %}
              %pattern = {{ ann[:pattern] || ann[0] }}
              %group = {{ ann[:group] || (:default) }}
              %async = {{ !(!ann[:async]) }}

              %handler = CallbackQueryHandler.new(%pattern, %group, %async, &->(c : Context) { client.{{ method.name.id }}(c) })
              client.add_event_handler(%handler)
            {% end %}
          {% end %}
        {% end %}
      {% end %}

{% if true %}
        {% for command_class in Tourmaline::Client.subclasses %}
          {% for method in command_class.methods %}

            # Handle `OnChosenInlineResult` annotation
            {% for ann in method.annotations(OnChosenInlineResult) %}
              %pattern = {{ ann[:pattern] || ann[0] }}
              %group = {{ ann[:group] || (:default) }}
              %async = {{ !(!ann[:async]) }}

              %handler = ChosenInlineResultHandler.new(%pattern, %group, %async, &->(c : Context) { client.{{ method.name.id }}(c) })
              client.add_event_handler(%handler)
            {% end %}
          {% end %}
        {% end %}
      {% end %}

handled = [] of String
@event_handlers.each do |handler|
  if handled.includes?(handler.group)
  else
    if handler.call(self, update)
      @persistence.handle_update(update)
      handled << handler.group
    end
  end
end

{% if true %}
        {% for command_class in Tourmaline::Client.subclasses %}
          {% for method in command_class.methods %}

            # Handle `Command` annotation
            {% for ann in method.annotations(Command) %}
              %command = {{ (ann.named_args[:command] || ann.named_args[:commands]) || ann.args[0] }}
              %prefix = {{ ann.named_args[:prefix] }}
              %group  = {{ ann.named_args[:group] || (:default) }}
              %async = {{ !(!ann[:async]) }}
              %private_only = {{ ann.named_args[:private_only] || false }}
              %group_only = {{ ann.named_args[:group_only] || false }}
              %admin_only = {{ ann.named_args[:admin_only] || false }}

              %handler = CommandHandler.new(
                %command,
                %prefix,
                %group,
                %async,
                %private_only,
                %group_only,
                %admin_only,
                &->(ctx : Context) { client.{{ method.name.id }}(ctx) }
              )

              client.add_event_handler(%handler)
            {% end %}
          {% end %}
        {% end %}
      {% end %}

@persisted_chats[chat.id] = chat
if username = chat.username
  @persisted_chat_ids[username] = chat.id
end
chat

@persisted_users[user.id] = user
if username = user.username
  @persisted_user_ids[username] = user.id
end
user

{% if true %}
        {% for command_class in Tourmaline::Client.subclasses %}
          {% for method in command_class.methods %}

            # Handle `Hears` annotation
            {% for ann in method.annotations(Hears) %}
              %pattern = {{ ann[:pattern] || ann[0] }}
              %group = {{ ann[:group] || (:default) }}
              %async = {{ !(!ann[:async]) }}

              %handler = HearsHandler.new(%pattern, %group, %async, &->(c : Context) { client.{{ method.name.id }}(c) })
              client.add_event_handler(%handler)
            {% end %}
          {% end %}
        {% end %}
      {% end %}

if message = update.message
  if (text = message.text) || (text = message.caption)
    if match = text.match(@pattern)
      context = Context.new(update, message, text, match)
      @proc.call(context)
      return true
    end
  end
end

{% if true %}
        {% for command_class in Tourmaline::Client.subclasses %}
          {% for method in command_class.methods %}

            # Handle `OnInlineQuery` annotation
            {% for ann in method.annotations(OnInlineQuery) %}
              %pattern = {{ ann[:pattern] || ann[0] }}
              %group = {{ ann[:group] || (:default) }}
              %async = {{ !(!ann[:async]) }}

              %handler = InlineQueryHandler.new(%pattern, %group, %async, &->(c : Context) { client.{{ method.name.id }}(c) })
              client.add_event_handler(%handler)
            {% end %}
          {% end %}
        {% end %}
      {% end %}

if File.file?(@filename)
  json = File.read(@filename)
  parsed = NamedTuple(users: Hash(Int64, User), user_ids: Hash(String, Int64), chats: Hash(Int64, Chat), chat_ids: Hash(String, Int64)).from_json(json)
  @persisted_users = parsed[:users]
  @persisted_user_ids = parsed[:user_ids]
  @persisted_chats = parsed[:chats]
  @persisted_chat_ids = parsed[:chat_ids]
end

if only_match?(context)
else
  return call_next(context)
end
if body = context.request.body
  update = Update.from_json(body)
  @bot.handle_update(update)
end

if @steps.empty?
  raise("No steps for this stage")
end
@active = true
@client.add_event_handler(@event_handler)
@on_start_handlers.each(&.call)
if @initial_step
  transition(@initial_step.to_s)
end
self

event = event.to_s
if @steps.has_key?(event)
else
  raise("Event does not exist")
end
@current_step = event
@response_awaiter = nil
@steps[@current_step].call(@client)
self

if (value = self[index_or_key]) && (value.responds_to?(:dig))
  return value.dig(*subkeys)
end
raise("#{self.class} value not diggable for key: #{index_or_key.inspect}")

